sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Player, function (sprite, otherSprite) {
    if (sprite == bossmagic) {
        lifeOfknight.value += -1
    }
    if (sprite == bossmagic2) {
        lifeOfknight.value += -1
    }
})
statusbars.onStatusReached(StatusBarKind.Health, statusbars.StatusComparison.LTE, statusbars.ComparisonType.Percentage, 50, function (status) {
    if (lifeOfboss.value == 0) {
        boss.setVelocity(0, 70)
        boss.setScale(3, ScaleAnchor.Middle)
    }
})
statusbars.onZero(StatusBarKind.Health, function (status) {
    if (lifeOfboss.value <= 0) {
        pause(1000)
        game.setGameOverMessage(true, "GOD KILLED")
        game.gameOver(true)
    }
    if (lifeOfknight.value == 0) {
        game.setGameOverMessage(false, "YOU DIED")
        game.gameOver(false)
    }
})
